mapper.insert:
INSERT INTO address (
    user_id, receiver_name, receiver_phone, province, city, district,
    detail_address, is_default, create_time, update_time
) VALUES (
             #{userId}, #{receiverName}, #{receiverPhone}, #{province}, #{city}, #{district},
             #{detailAddress}, #{isDefault}, #{createTime}, #{updateTime}
         )

mapper.update:
UPDATE address
receiver_name = #{receiverName}, receiver_phone = #{receiverPhone}, province = #{province}, city = #{city}, district = #{district}, detail_address = #{detailAddress}, is_default = #{isDefault}, update_time = NOW()
WHERE id = #{id} AND user_id = #{userId}

UPDATE address
SET is_default = 0, update_time = NOW()
WHERE user_id = #{userId}
