mapper.insert:
INSERT INTO `show` (
    name, venue, region, category, start_time, end_time, total_tickets,
    available_tickets, price, session_info, ticket_tier, is_on_sale, status, create_time, update_time
) VALUES (
             #{name}, #{venue}, #{region}, #{category}, #{startTime}, #{endTime}, #{totalTickets},
             #{availableTickets}, #{price}, #{sessionInfo}, #{ticketTier}, #{isOnSale}, #{status}, #{createTime}, #{updateTime}
         )

mapper.update:
UPDATE `show`
name = #{name}, venue = #{venue}, region = #{region}, category = #{category}, start_time = #{startTime}, end_time = #{endTime}, total_tickets = #{totalTickets}, price = #{price}, session_info = #{sessionInfo}, ticket_tier = #{ticketTier}, is_on_sale = #{isOnSale}, status = #{status}, update_time = NOW()
WHERE id = #{id}

mapper.select:
SELECT
    id, name, venue, region, category, start_time, end_time, total_tickets,
    available_tickets, price, session_info, ticket_tier, is_on_sale, status, create_time, update_time
FROM `show`
WHERE status = 1

    AND region = #{region}


    AND category = #{category}

ORDER BY start_time ASC

    LIMIT #{limit}

SELECT
    id, name, venue, region, category, start_time, end_time, total_tickets,
    available_tickets, price, session_info, ticket_tier, is_on_sale, status, create_time, update_time
FROM `show`
WHERE status = 1

    AND region = #{region}


    AND category = #{category}

ORDER BY start_time ASC
LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(id)
FROM `show`
WHERE status = 1

    AND region = #{region}


    AND category = #{category}

SELECT
    id, name, venue, region, category, start_time, end_time, total_tickets,
    available_tickets, price, session_info, ticket_tier, is_on_sale, status, create_time, update_time
FROM `show`


        AND name LIKE CONCAT('%', #{name}, '%')


        AND region = #{region}


        AND category = #{category}


        AND status = #{status}


ORDER BY create_time DESC
LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(id)
FROM `show`


        AND name LIKE CONCAT('%', #{name}, '%')


        AND region = #{region}


        AND category = #{category}


        AND status = #{status}
