mapper.insert:
INSERT INTO `show` (
    name, venue, start_time, end_time, total_tickets,
    available_tickets, price, status, create_time, update_time
) VALUES (
             #{name}, #{venue}, #{startTime}, #{endTime}, #{totalTickets},
             #{availableTickets}, #{price}, #{status}, #{createTime}, #{updateTime}
         )

mapper.update:
UPDATE `show`
name = #{name}, venue = #{venue}, start_time = #{startTime}, end_time = #{endTime}, total_tickets = #{totalTickets}, price = #{price}, status = #{status}, update_time = NOW()
WHERE id = #{id}
