mapper.update:
UPDATE user
username = #{username}, password = #{password}, email = #{email}, phone = #{phone}, real_name = #{realName}, role = #{role}, status = #{status}, update_time = NOW()
WHERE id = #{id}

mapper.select:
SELECT id, username, password, email, phone, real_name, role, status, create_time, update_time FROM user
AND username LIKE CONCAT('%', #{username}, '%')
AND status = #{status}
ORDER BY create_time DESC LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(id) FROM user
AND username LIKE CONCAT('%', #{username}, '%')
AND status = #{status}
